import React from 'react';
import { useNavigate } from 'react-router-dom';

const plans = [
  {
    name: 'Free',
    price: 0,
    page_limit: 1,
    description: "Générez un mémoire d'une page gratuitement. Export Word/PDF, preview, éditeur moderne.",
    features: [
      'Génération IA limitée',
      'Export Word/PDF',
      'Preview',
      'Éditeur Markdown'
    ]
  },
  {
    name: 'Starter',
    price: 9,
    page_limit: 20,
    description: "Jusqu'à 20 pages générées par mois. Génération IA avancée, enrichissement, support prioritaire.",
    features: [
      'Génération IA avancée',
      'Export Word/PDF',
      'Enrichissement de sections',
      'Support prioritaire'
    ]
  },
  {
    name: 'Pro',
    price: 19,
    page_limit: 100,
    description: "Jusqu'à 100 pages générées par mois. Génération et enrichissement illimités, export avancé.",
    features: [
      'Génération illimitée',
      'Enrichissement illimité',
      'Export avancé',
      'Table des matières interactive'
    ]
  },
  {
    name: 'Business',
    price: 49,
    page_limit: null,
    description: "Pages illimitées, gestion d'équipe, statistiques, support premium.",
    features: [
      'Accès équipe',
      'Gestion multi-utilisateurs',
      'Statistiques',
      'Support premium',
      'Licences multiples'
    ]
  }
];

const Plans = () => {
  const navigate = useNavigate();
  return (
    <div className="max-w-5xl mx-auto py-12 px-4 font-sans">
      <h1 className="text-4xl font-extrabold text-primary-400 mb-8 text-center drop-shadow">Nos offres & tarifs</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
        {plans.map(plan => (
          <div key={plan.name} className="bg-dark-300/80 rounded-2xl shadow-card border border-dark-400/40 p-8 flex flex-col items-center">
            <h2 className="text-2xl font-bold text-primary-400 mb-2">{plan.name}</h2>
            <div className="text-3xl font-extrabold text-white mb-2">{plan.price === 0 ? 'Gratuit' : `$${plan.price}/mois`}</div>
            <div className="text-primary-300 mb-4 text-center min-h-[48px]">{plan.description}</div>
            <ul className="mb-6 space-y-1 text-sm text-primary-200 text-left w-full">
              {plan.features.map((f, i) => (
                <li key={i} className="flex items-center"><span className="mr-2">✔️</span>{f}</li>
              ))}
            </ul>
            <button
              onClick={() => navigate('/register', { state: { plan: plan.name.toLowerCase() } })}
              className="w-full py-3 px-4 rounded-xl bg-gradient-to-r from-primary-500 to-primary-700 text-white font-semibold shadow-card hover:from-primary-600 hover:to-primary-800 transition-all duration-200"
            >
              {plan.price === 0 ? 'Commencer gratuitement' : 'Choisir ce plan'}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Plans;
